import React, { useState, useEffect, useContext } from 'react'
import { WebState } from './appstate'

const Panel2 = (props) => {
    const context = useContext(WebState)
    const { activities, handleEnd, currentTime } = context

    const duration = (item) => {
        const { today } = item
        const startDate = new Date(today)
        const endDate = new Date(item.endDate)

        if (item.endTime) {
            return handleTimeState(startDate, endDate)
        } else {

            return handleTimeState(today);
        }


    }

    const handleTimeState = (starttime, endTime = currentTime) => {
        const time1 = new Date(starttime)
        const time2 = new Date(endTime)
        let seconds = Math.floor((time2 - time1) / 1000) || 0;
        let minutes = Math.floor(seconds / 60) || 0
        if (minutes < 10) {
            minutes = `0${minutes.toString()}`
        }
        let hours = Math.floor(minutes / 60) || 0
        if (hours < 10) {
            hours = `0${hours.toString()}`
        }
        let remainderSeconds = seconds % 60
        if (remainderSeconds < 10) {
            remainderSeconds = `0${remainderSeconds.toString()}`
        }
        return `${hours}:${minutes}:${remainderSeconds}`

    }

    const endActivity = (event, key) => {
        event.preventDefault()

        handleEnd(key, currentTime)
    }

    useEffect(() => {
        const interval = setInterval(() => {
            setCurrentTime(new Date())
        }, 1000);
        return () => clearInterval(interval);
    }, []);

    return (
        <div className = 'panel2'>
            <table>
                <thead>
                    <tr>
                        <th>Start Time</th>
                        <th>End Time</th>
                        <th>Duration</th>
                        <th>Description</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        activities.map(activity => (
                            <tr key={activities.indexOf(activity)}>
                                <td>
                                    {activity.time}
                                </td>
                                <td>
                                    {activity.endTime || ''}
                                </td>
                                <td>
                                    {duration(activity)}
                                </td>
                                <td>
                                    {activity.description}
                                </td>
                                <button onClick={(e) => endActivity(e, activities.indexOf(activity))}>End Activity</button>
                            </tr>

                        ))
                    }
                </tbody>
            </table>
        </div>
    )
}

export default Panel2
