import React, {useState, useContext} from 'react'
import {WebState} from './appstate'

const Panel3 = () => {
    const context = useContext(WebState);
    const {activities} = context;
    const [options, setOptions] = useState(0)
    const [description, setDescription] = useState('')
    const handleChange = (event) =>{
        const {value} = event.target
        setOptions(value)
        console.log(value)
        setDescription(activities[value].description)

    }
    return (
        <div>
            <select className="instrumentbox" value={options} onChange={handleChange}>
                {
                    activities.map(activity =>(
                        <option value={activities.indexOf(activity)}>{activity.title}</option>

                    ))
                }

                    </select>
        </div>
    )
}

export default Panel3
