import React, { useState, useEffect, useContext } from 'react'
import { WebState } from './appstate'

const Panel2 = (props) => {
    const context = useContext(WebState)
    const { activities, handleEnd, currentTime, handleTimeState } = context

    const duration = (item) => {
        const { today } = item
        const startDate = new Date(today)
        const endDate = new Date(item.endDate)

        if (item.endTime) {
            return handleTimeState(startDate, endDate)
        } else {

            return handleTimeState(today);
        }


    }



    const endActivity = (event, key) => {
        event.preventDefault()

        handleEnd(key, currentTime)
    }



    return (
        <div className = 'panel2'>
            <table>
                <thead>
                    <tr>
                        <th>Start Time</th>
                        <th>End Time</th>
                        <th>Duration</th>
                        <th>Description</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        activities.map(activity => (
                            <tr key={activities.indexOf(activity)}>
                                <td>
                                    {activity.time}
                                </td>
                                <td>
                                    {activity.endTime || ''}
                                </td>
                                <td>
                                    {duration(activity)}
                                </td>
                                <td>
                                    {activity.description}
                                </td>
                                <button onClick={(e) => endActivity(e, activities.indexOf(activity))}>End Activity</button>
                            </tr>

                        ))
                    }
                </tbody>
            </table>
        </div>
    )
}

export default Panel2
