import React, { useState, useEffect, useContext } from 'react'
import { WebState } from './appstate'

const Panel2 = (props) => {
    const context = useContext(WebState)
    const {activities} = context
    const [currentTime, setCurrentTime] = useState(new Date())

    const duration =  (startTime) =>{
        const timeDiff = currentTime - startTime;
        return `${timeDiff.getHours()}:${timeDiff.getMinutes()}:${timeDiff.getSeconds()}`
    }
    return (
        <div>
            <table>
                <thead>
                    <tr>
                        <th>Start Time</th>
                        <th>End Time</th>
                        <th>Duration</th>
                        <th>Description</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        activities.map(activity => (
                    <tr>
                            <td>
                                {activity.time}
                            </td>
                            <td>
                                {activity.EndTime || ''}
                            </td>
                            <td>
                                {duration(activity.today)}
                            </td>
                            <td>
                                {activity.description}
                            </td>
                            <button>End Activity</button>
                    </tr>

                        ))
                    }
                </tbody>
            </table>
        </div>
    )
}

export default Panel2
